{
    "query": "query sessions($token: CustomerToken!, $limit: Int, $offset: Int) {sessions(token: $token, limit: $limit, offset: $offset) {session_id type status videos { videos { video_id name start_time duration uploaded_time width height fps } } } }",
    "variables": {
        "token": {
            "customer_id": {{customer_id}},
            "token": "{{api_key}}"
        },
        "limit": null,
        "offset": null
    }
}

{
    "query": "mutation ($token: CustomerToken!, $sessionData: SessionInput!) {createSession(token: $token, sessionData: $sessionData) { success error session { session_id type status } } }",
    "variables": {
        "token": {
            "customer_id": {{customer_id}},
            "token": "{{api_key}}"
        },
        "sessionData": {
            "type": "soccer_game",
            "game_info": {
                "home_team": {
                    "name": "Blue",
                    "score": 0,
                    "color": "#0000ff"
                },
                "away_team": {
                    "name": "Red",
                    "score": 0,
                    "color": "#ff0000"
                },
                "start_time": "2024-01-01T12:00:00Z"
            },
            "capabilities": [
                "tracking", "highlights"
            ]
        }
    }
}

{
    "query": "mutation ( $token: CustomerToken! $session_id: Int! $video_name: String! $start_time: DateTime ) { uploadVideo( token: $token session_id: $session_id video_name: $video_name start_time: $start_time ) { success error upload_url } }",
    "variables": {
        "token": {
            "customer_id": {{customer_id}},
            "token": "{{api_key}}"
        },
        "session_id": {{session_id}},
        "video_name": "video.mp4",
        "start_time": "2024-01-01T12:00:00Z"
    }
}

{
    "query": "query ($token: CustomerToken!, $session_id: Int!) {session(token: $token, session_id: $session_id) { session_id type status } }",
    "variables": {
        "token": {
            "customer_id": {{customer_id}},
            "token": "{{api_key}}"
        },
        "session_id": {{session_id}}
    }
}

{
    "query": "query ($token: CustomerToken!, $session_id: Int!) { sessionResult(token: $token, session_id: $session_id) { objects { object_id type side tracking_url } highlights { highlight_id video_id start_offset duration tags video_stream } } }",
    "variables": {
        "token": {
            "customer_id": {{customer_id}},
            "token": "{{api_key}}"
        },
        "session_id": {{session_id}}
    }
}
